import { useCoinbaseWallet } from "@thirdweb-dev/react"

const App = () => {
  const connectWithCoinbaseWallet = useCoinbaseWallet()

  return (
    <button onClick={connectWithCoinbaseWallet}>
      Connect Coinbase Wallet
    </button>
  )
}

