import { useNetwork, ChainId } from "@thirdweb-dev/react";

const App = () => {
  const [, switchNetwork] = useNetwork();

  return (
    <button onClick={() => switchNetwork(ChainId.Polygon)}>
       Switch Network
    </button>
  );
};

